var gulp = require('gulp');
var stylus = require('gulp-stylus');

gulp.task('styles', async function () {
    gulp.src('style.styl')
        .pipe(stylus({
            use: []
        }))
        .pipe(gulp.dest("./"));
});

gulp.task('watch:styles', async function () {
    gulp.watch('**/*.styl', gulp.series('styles'));
});

